declare class AgentCore {
    readonly config: AgentConfig;
    private entrypoints;
    constructor(config: AgentConfig);
    addEntrypoint<TInput extends z.ZodTypeAny | undefined = z.ZodTypeAny | undefined, TOutput extends z.ZodTypeAny | undefined = z.ZodTypeAny | undefined>(entrypoint: EntrypointDef<TInput, TOutput>): void;
    getEntrypoint(key: string): EntrypointDef | undefined;
    listEntrypoints(): EntrypointDef[];
}
declare function createAgentCore(config: AgentConfig): AgentCore;

declare class AgentBuilder {
    private meta;
    private extensions;
    private entrypoints;
    constructor(meta: AgentMeta);
    use<E extends Extension>(ext: E): this;
    addEntrypoint(def: EntrypointDef): this;
    build(): Promise<AgentRuntime>;
}

/**
 * Creates a new agent builder for constructing an agent runtime with extensions.
 *
 * @example
 * ```typescript
 * const identity = identity({ trust });
 * const payments = payments({ config });
 * const a2a = a2a();
 *
 * const agent = createAgent(meta)
 *   .use(identity)
 *   .use(payments)
 *   .use(a2a)
 *   .build();
 * ```
 */
declare function createAgent(meta: AgentMeta): AgentBuilder;

/**
 * Validates required agent metadata and throws descriptive errors if invalid.
 * @param meta - Agent metadata to validate
 * @throws Error if required fields are missing
 */
declare function validateAgentMetadata(meta: AgentMeta): void;

export { AgentBuilder, AgentCore, createAgent, createAgentCore, validateAgentMetadata };
