/**
 * AP2 (Agent Payments Protocol) role types.
 */
type AP2Role = 'merchant' | 'shopper' | 'credentials-provider' | 'payment-processor';
/**
 * Parameters for AP2 extension configuration.
 */
type AP2ExtensionParams = {
    roles: [AP2Role, ...AP2Role[]];
    [key: string]: unknown;
};
/**
 * Descriptor for AP2 extension in agent manifest.
 */
type AP2ExtensionDescriptor = {
    uri: 'https://github.com/google-agentic-commerce/ap2/tree/v0.1';
    description?: string;
    required?: boolean;
    params: AP2ExtensionParams;
};
/**
 * Configuration for AP2 (Agent Payments Protocol) extension.
 */
type AP2Config = {
    roles: AP2Role[];
    description?: string;
    required?: boolean;
};
/**
 * AP2 runtime type.
 * Returned by AgentRuntime.ap2 when AP2 is configured.
 */
type AP2Runtime = {
    readonly config: AP2Config;
};

export type { AP2Config, AP2ExtensionDescriptor, AP2ExtensionParams, AP2Role, AP2Runtime };
